  };
  return (
    <Layout>
      <section className="shadow-blue white-bg padding mt-0">
        <ul className="list-inline portfolio-info mt-0">
          <li className="list-inline-item">
            <i className="icon-user"></i>
            {meta.author?.username ? meta.author.username : "Loading..."}
          </li>
          <li className="list-inline-item">
            <i className="icon-calendar"></i>
            {/* {meta.date} */}
          </li>
          <li className="list-inline-item">
            <i className="icon-folder"></i>
            {/* {meta.category} */}
          </li>
        </ul>
        <div className="blog-content mt-4">
          <Markdown children={meta.content ? meta.title + meta.content : "Loading..."}></Markdown>
        </div>
        <div className="mi-blog-details-comments mt-4">
          <form>
            <button
              className={
                isPostLiked ? "blog-favorite-btn-liked" : "blog-favorite-btn"
              }
              onClick={likeHandler}
              type="submit"
            >
              <Favorite /> 117
            </button>
          </form>
          {/* <Disqus.DiscussionEmbed
            shortname={disqusShortname}
            config={disqusConfig}
          /> */}
        </div>
      </section>
    </Layout>
  );
}

export default BlogDetails;
