import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import userRequest from "./userRequests";
import Layout from "../components/Layout/Layout"; //Use and import Layout2 when you use multipage

const images = {
    Pika: "/../../public/pika.gif"
}
// import Pika from "/../../public/pika.gif"

const Login = ({ setCurrentUser }) => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const { email, password } = formData;
  const [errors, setErrors] = useState({});

  const loginHandler = async (e) => {
    e.preventDefault();
    const user = {
      email,
      password,
    };
    try {
      await userRequest
        .post("/login/", user)
        .then(() => {
          // Request succeeded, perform any necessary actions
          setCurrentUser(user);
        })
        .catch((error) => {
          // Request failed, handle the error
          setErrors({});
          if (error.response.data[0]) {
            setErrors({
              email: error.response.data[0],
              password: error.response.data[0],
            });
          } else {
            console.log(error);
          }
        });
    } catch (error) {
      console.log(error);
    }
  };

  const changeHandler = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <Layout>
      <section className="shadow-blue white-bg padding mt-0">
        <h1 style={{ fontSize: "88px" }}>Login</h1>
        <p style={{ fontSize: "22px" }}>nathanfoss.dev <b style={{ color: "#085786" }}>web</b></p>
        <img src={Pika} alt="Pikachu running" width="70%" height="auto" />
      </section>
    </Layout>
  );
};
export default Login;
